    def self.to_tags(group_name, attr_name)
      tags = []

      if @@valid_group_names_for_tags.keys.include?(group_name)
        # map group name to a tag
        tags += TagGroup::validate_and_clean_string(@@valid_group_names_for_tags[group_name])
      end

      if @@valid_group_attrs_for_tags.include?(group_name)
        # use group attribute name as a tag
        tags += TagGroup::validate_and_clean_string(attr_name)
      end

      if brand?(group_name) and @@valid_brand_names_for_tags.keys.include?(brand_category = brand_category(group_name))
        # map brand group name to a tag
        tags += TagGroup::validate_and_clean_string(@@valid_brand_names_for_tags[brand_category])
      end
      
      if brand?(group_name) and @@valid_brand_name_attrs.include?(brand_category = brand_category(group_name))
        # use brand attribute as a tag
        tags += TagGroup::validate_and_clean_string(attr_name)
      end

      if cuisine?(group_name)
        # use attribute name to build the tag - e.g. "Chinese" + "food"
        # note: strip 'food' from attr_name if it already exists
        tags += TagGroup::validate_and_clean_string("#{attr_name.gsub(/food/i, '').strip} food")
      end

      tags.uniq.sort
    end

    def self.brand?(group_name)
      group_name.match(/^Brands -/)
    end

    def self.brand_category(group_name)
      m = group_name.match(/^Brands - ([\s\w&,]*)/)
      m[1]
    end

    def self.cuisine?(group_name)
      group_name.match(/^Cuisine$/)
    end

  end
  
